import React from "react";
import "../styles.css";
import Logo from "../Logo/Logo";
import Footer from "../Footer";
const inputs = document.querySelector("input");
const form = document.querySelector("#contact_form");
const small = document.querySelector("#error");
function showError() {
  for (let i = 0; i < inputs.length; i++) {
    if (inputs.trim().value === " ") {
    }
  }

  form.addEventListener("submit", (e) => {
    showError();
    e.preventDefault();
  });
}
export default function Contact() {
  return (
    <div className="contact-page">
      <Logo />

      <section id="form">
        <form id="contact_form">
          <p>contact Suprime</p>
          <br />
          <small id="error">fields cannot be empty</small>
          <div className="input-rows">
            <input
              type="text"
              name="first-name"
              id="first-name"
              placeholder="first name"
            />

            <input
              type="text"
              name="last-name"
              id="last-name"
              placeholder="last name"
            />

            <input type="email" name="email" id="email" placeholder="email" />

            <input
              type="hidden"
              name="order number"
              id="order number"
              placeholder="order number"
            />
          </div>
          <select id="dropdown" name="dropdown">
            <option value="order">online order status requests</option>
            <option value="order">online return or exchange request</option>
            <option value="order">online order lost package</option>
            <option value="1">general inquiries</option>
            <option value="2">press</option>
          </select>
          <label id="textarea">message</label>
          <textarea name="message" id="message" cols="40" rows="10"></textarea>
          <button id="btn" type="submit">
            send
          </button>
        </form>
      </section>
      <Footer />
    </div>
  );
}
