  const [items, setItems] = useState([]);
  const [images, setImages] = useState([]);
  const globalCart = createContext();

  useEffect(() => {
    setItems(inventory);
    setImages(image);
  }, [items, images]);

  console.log(images);

  return (
    <div>
      <img alt="product category" src="" />
