  const [items, setItems] = useState([]);
  const [images, setImages] = useState([]);
  const globalCart = createContext();

  useEffect(() => {
    setItems(inventory);
    setImages(image);
  }, [items, images]);
  console.log(image);
  return (
    <div id="card">
      <p>Item description goes here</p>
    </div>
  );
}

export default ProductDetail;
