      {images1.map(({ front }) => {
        return (
          <>
            <a href={`/shop/${item.category}`}>
              <img src={front} alt={item.item_name} />
            </a>
            <p>{item.item_name}</p>
            <button id="sub-btn" onClick={addToCart}>
              add to cart
