export default function Contact() {
  const [vals, setVals] = useState({
    firstname: "",
    lastname: "",
    email: "",
    ordernumber: "",
    choices: "",
    message: "",
  });

  const inputs = [
    {
      id: 1,
      name: "firstname",
      type: "text",
      placeholder: "firstname",
      required: true,
    },
    {
      id: 2,
      name: "lastname",
      type: "text",
      placeholder: "lastname",
      required: true,
    },
    {
      id: 3,
      name: "firstname",
      type: "text",
      placeholder: "firstname",
      required: true,
    },
    {
      id: 4,
      name: "email",
      type: "email",
      placeholder: "email",
      required: true,
    },
    {
      id: 5,
      name: "message",
      type: "textarea",
      placeholder: "firstname",
      required: true,
    },
  ];

  const handleSend = (e) => {
    e.prevent.Default();
  };
  return (
    <div className="contact-page">
      <Logo />
      <form onSubmit={handleSend}>
        <FormInputs placeholder="firstname" setVals={setVals} />
        <button is="sub-btn">send</button>
      </form>

      <Footer />
    </div>
  );
}
