import React from "react";

export default function ProductDetail({ item }) {
  const options = [item];
  console.log(options);
  return (
    <div className="product-details">
      <p>{item.item_name}</p>
      <p>{item.description}</p>
      <p>${item.price}</p>
      <select name="sizes" id="sizeList"></select>
      {[...item(item.quantity).keys()].map((x) => (
        <option>{x + 1}</option>
      ))}
    </div>
  );
}
