import React from "react";

function Product({ stock, children }) {
  return (
    <div>
      {stock.map(({ items, index, array }) => {
        return (
          <div>
            <img className="imgs" src={items.images} alt={items.item_name} />
            <h4>{items.item_name}</h4>
            <p>{items.description}</p>
            <div>${items.price}</div>
          </div>
        );
      })}
    </div>
  );
}
export default Product;
