import React from "react";
import StockData from "../../Data/inventory.json";
import ImageData from "../../Data/images.json";
import { useState, createContext, useEffect } from "react";
import ProductDetail from "./ProductDetail";

function Product() {
  const [items, setItems] = useState([]);
  const [images, setImages] = useState([]);
  const globalCart = createContext([]);

  useEffect(() => {
    setImages(ImageData);
    setItems(StockData);
  }, []);

  return (
    <div id="items-grid">
      {images.map((imageDetail) => {
        return (
          <div id="products-grid">
            <a key={imageDetail.id} href="/shop/">
              <img key={imageDetail.id} id="products" src={imageDetail.front} />
            </a>
            <span>{items.item_name}</span>

            <button id="sub-btn">add to cart</button>
          </div>
        );
      })}
      <ProductDetail key={items.id} items={items} images={images} />
    </div>
  );
}

export default Product;
