import React from "react";
import { useState } from "react";
import Product from "../../Products/Product";
import ProductList from "../../Products/ProductList";
import Footer from "../../Footer";
import Logo from "../../Logo/Logo";
import id from "../../../App";
import ShopTabs from "./ShopTabs";

export default function Shop({ items, setItems }) {
  const [searchItem, setSearchItem] = useState("");
  const [searchResult, setSearchResult] = useState(items);
  const findProducts = (e) => {
    setSearchItem(e.target.value);

    const newItems = items.filter((item) => {
      if (searchItem == "") {
        return item;
      } else {
        return item.item_name.includes(searchItem);
      }
    });
    setSearchResult(newItems);
    console.log(searchItem);
  };
  return (
    <>
      <input
        value={searchItem}
        placeholder="Search.."
        onChange={findProducts}
      />
      <div id="shop-logo">
        <Logo />
      </div>
      <ShopTabs />
      <div>
        <ProductList key={id} items={items}>
          {searchResult.map((item) => (
            <Product
              key={id}
              items={item}
              src={item.images}
              alt={item.item_name}
              href={`/shop/${item.category}/${item.id}`}
            />
          ))}
        </ProductList>
      </div>
      <Footer className="sticky" />
    </>
  );
}
