import React from "react";

export default function ProductDetail({ item }) {
  return (
    <div className="product-details">
      <p>{item.item_name}</p>
      <p>{item.description}</p>
      <p>${item.price}</p>
      <select name="sizes" id="sizeList">
        {item.map(({ sizes }) => {
          return <option>{sizes}</option>;
        })}
      </select>
    </div>
  );
}
